mapper.select:
select count(*) as total
from user
where userid = #{ id }

mapper.insert:
insert into board (userid,title,kind,note,pname,fname) 
values (#{userid},#{title},#{kind},#{note},

	#{pname},#{fname}


	null, null

)

mapper.update:
update board set title = #{ title }, note = #{ note },
pname = #{pname}, fname = #{fname},
kind = #{ kind } where no = #{ no }
