mapper.insert:
insert into board(userid, title, kind, note, pname, fname)
values(#{userid},#{title}, #{kind}, #{note},

 #{pname}, #{fname}


 null, null

 )

mapper.update:
update board set title = #{title}, note = #{note}, kind = #{kind},
pname = #{pname}, fname = #{fname}
where no = #{no}

mapper.select:
select no,title,date(wdate) as wdate,hit, (select name from user where userid = board.userid) as name, (select count(*) from reply where no = board.no) as rcount from board where kind = #{ kind }
and title like '%${ keyword }%'
order by no desc limit #{offset},10
